package JavaSessions4;

public class LoopsAssignment2 {
	/*
	 * 2. WAP to print following output: I am Batman 1
	 * 
	 * I am Batman 2
	 * 
	 * I am Batman 3
	 * 
	 * I am Batman 4
	 * 
	 * I am Batman 5
	 * 
	 * I am Batman 6
	 * 
	 * I am Batman 7
	 * 
	 * I am Batman 8
	 * 
	 * I am Batman 9
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "I am Batsman";
		for (int i = 1; i <= 9; i++) {
			System.out.println(str + " " + i);
		}
	}

}
