package JavaSessions4;

public class LoopsAssignment3 {
	/*
	 * 3. WAP to print 10 to 1 using for, while and do-while loop
	 */
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		while (i > 0) {
			System.out.println(i);
			i--;
		}

		i = 10;
		do {
			System.out.println(i);
			i--;
		} while (i > 0);

	}

}
