package JavaSessions5;

public class Array3 {

	/*
	 * Question 3: Try to print the following pattern on the console:
	 *  n = 4 
	 *  n = 3 
	 *  n = 2 
	 *  n = 1 
	 *  n = 0
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=4;
		while(n>=0) {
			System.out.println("n="+n);
			n--;
		}
	}

}
